import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Decisions from './Decisions';
import Suggestions from './Suggestions';

function App() {
  const [complaints, setComplaints] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  // دالة لجلب الشكاوى من الـAPI عند تحميل المكون
  useEffect(() => {
    const fetchComplaints = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/complaints');
        setComplaints(response.data);
      } catch (error) {
        console.error('Error fetching complaints:', error);
      }
    };
    fetchComplaints();
  }, []);

  // دالة لإرسال شكوى جديدة إلى الـAPI
  const handleSubmit = async (e) => {
    e.preventDefault(); // منع إعادة تحميل الصفحة
    try {
      const newComplaint = { title, description };
      await axios.post('http://localhost:5000/api/complaints', newComplaint);
      setTitle('');
      setDescription('');

      // إعادة جلب الشكاوى بعد الإضافة لعرضها على الفور
      const response = await axios.get('http://localhost:5000/api/complaints');
      setComplaints(response.data);
    } catch (error) {
      console.error('Error submitting complaint:', error);
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>

      {/* نموذج تقديم شكوى جديدة */}
      <h1>تقديم شكوى جديدة</h1>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '10px' }}>
          <label htmlFor="title" style={{ display: 'block' }}>عنوان الشكوى:</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            style={{ width: '100%', padding: '8px' }}
          />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label htmlFor="description" style={{ display: 'block' }}>وصف الشكوى:</label>
          <textarea
            id="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
            rows="4"
            style={{ width: '100%', padding: '8px' }}
          ></textarea>
        </div>
        <button type="submit" style={{ padding: '10px 20px', cursor: 'pointer' }}>إرسال الشكوى</button>
      </form>

      <hr style={{ margin: '30px 0' }} />

      {/* قسم عرض الشكاوى المقدمة */}
      <h1>الشكاوى المقدمة</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
        {complaints.map(complaint => (
          <div key={complaint.id} style={{ border: '1px solid #ccc', padding: '15px', borderRadius: '8px', width: '300px' }}>
            <h2 style={{ marginTop: '0' }}>{complaint.title}</h2>
            <p>{complaint.description}</p>
            <p><strong>الحالة:</strong> {complaint.status}</p>
          </div>
        ))}
      </div>

      <hr style={{ margin: '30px 0' }} />

      {/* قسم عرض قرارات البلدية */}
      <Decisions />

    </div>
  );
}

export default App;
