import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  // حالة لتخزين قائمة الشكاوى
  const [complaints, setComplaints] = useState([]);

  // استخدام useEffect لجلب البيانات عند تحميل المكون
  useEffect(() => {
    // دالة لجلب الشكاوى من الـAPI
    const fetchComplaints = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/complaints');
        setComplaints(response.data);
      } catch (error) {
        console.error('Error fetching complaints:', error);
      }
    };

    fetchComplaints();
  }, []); // [] يعني أن هذه الدالة ستعمل مرة واحدة فقط عند تحميل المكون

  return (
    <div className="App">
      <h1>الشكاوى المقدمة</h1>
      <ul>
        {complaints.map(complaint => (
          <li key={complaint.id}>
            <h2>{complaint.title}</h2>
            <p>{complaint.description}</p>
            <p>الحالة: {complaint.status}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
