import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Suggestions() {
  const [suggestions, setSuggestions] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  useEffect(() => {
    const fetchSuggestions = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/suggestions');
        setSuggestions(response.data);
      } catch (error) {
        console.error('Error fetching suggestions:', error);
      }
    };
    fetchSuggestions();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const newSuggestion = { title, description };
      await axios.post('http://localhost:5000/api/suggestions', newSuggestion);
      setTitle('');
      setDescription('');
      const response = await axios.get('http://localhost:5000/api/suggestions');
      setSuggestions(response.data);
    } catch (error) {
      console.error('Error submitting suggestion:', error);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>تدوين الاقتراحات</h1>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '10px' }}>
          <label htmlFor="suggestionTitle" style={{ display: 'block' }}>عنوان الاقتراح:</label>
          <input
            type="text"
            id="suggestionTitle"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            style={{ width: '100%', padding: '8px' }}
          />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label htmlFor="suggestionDescription" style={{ display: 'block' }}>وصف الاقتراح:</label>
          <textarea
            id="suggestionDescription"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
            rows="4"
            style={{ width: '100%', padding: '8px' }}
          ></textarea>
        </div>
        <button type="submit" style={{ padding: '10px 20px', cursor: 'pointer' }}>إرسال الاقتراح</button>
      </form>

      <hr style={{ margin: '30px 0' }} />

      <h2>قائمة الاقتراحات</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
        {suggestions.map(suggestion => (
          <div key={suggestion.id} style={{ border: '1px solid #ccc', padding: '15px', borderRadius: '8px', width: '300px' }}>
            <h3 style={{ marginTop: '0' }}>{suggestion.title}</h3>
            <p>{suggestion.description}</p>
            {/* عرض تاريخ الإضافة إذا كان متوفراً */}
            {suggestion.createdAt && <p><strong>التاريخ:</strong> {new Date(suggestion.createdAt._seconds * 1000).toLocaleDateString()}</p>}
          </div>
        ))}
      </div>
    </div>
  );
}

export default Suggestions;
