import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Decisions() {
  const [decisions, setDecisions] = useState([]);

  useEffect(() => {
    const fetchDecisions = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/decisions');
        setDecisions(response.data);
      } catch (error) {
        console.error('Error fetching decisions:', error);
      }
    };
    fetchDecisions();
  }, []);

  return (
    <div style={{ padding: '20px' }}>
      <h1>أهم مقررات البلدية</h1>
      {decisions.map(decision => (
        <div key={decision.id} style={{ border: '1px solid #ddd', padding: '15px', marginBottom: '15px', borderRadius: '8px' }}>
          <h2>{decision.title}</h2>
          <p><strong>التاريخ:</strong> {decision.date}</p>
          <p>{decision.details}</p>
        </div>
      ))}
    </div>
  );
}

export default Decisions;
